css:
/* Change weather text here */

#interface #current::after {
 content: 'C on Earth.'
}

/* the rest */

#interface { color: #dfe1cc!important }
#interface #time #date { margin: 0 50px }
#interface #clock-wrapper {
 display: flex;
 align-items: center;
 flex-wrap: wrap;
	gap: 1em;
}

#interface .clock-date {
	display: flex;
}

#show-settings button img {
 filter: sepia(1)
}
